<?php
use yii\bootstrap5\ActiveForm;
use yii\bootstrap5\Html;
use yii\helpers\Url;

 $this->title = 'FiscalBridge Portal Login';
 $this->context->layout = false;

 // Get security stamp from session (generated by controller)
 $securityAnswer = Yii::$app->session->get('security_stamp_answer');
 $securityQuestion = Yii::$app->session->get('security_stamp_question');
 
 // If not set, generate one (fallback)
 if (!$securityAnswer || !$securityQuestion) {
     $number1 = rand(5, 15);
     $number2 = rand(8, 25);
     $correctAnswer = $number1 + $number2;
     $question = "$number1 + $number2";
     Yii::$app->session->set('security_stamp_answer', $correctAnswer);
     Yii::$app->session->set('security_stamp_question', $question);
 } else {
     // Parse question to extract numbers for display
     preg_match('/(\d+)\s*\+\s*(\d+)/', $securityQuestion, $matches);
     $number1 = isset($matches[1]) ? (int)$matches[1] : rand(5, 15);
     $number2 = isset($matches[2]) ? (int)$matches[2] : rand(8, 25);
     $correctAnswer = $securityAnswer;
 }
?>
