            <div id="caps-lock-warning" class="caps-lock-warning">
                <small><i class="bi bi-exclamation-triangle"></i> Caps Lock is ON</small>
            </div>

            <div class="strength-meter" id="strength-meter">
                <div class="strength-meter-fill" id="strength-meter-fill"></div>
            </div>
            <div class="strength-labels">
                <span>Weak</span>
                <span>Medium</span>
                <span>Strong</span>
            </div>
            <div class="password-strength-text" id="password-strength-text"></div>

            <div class="form-group">
                <label class="form-label"><i class="bi bi-shield-check"></i> Security Stamp</label>
                <div class="security-stamp-wrapper">
                    <span id="security-question"><?= $securityQuestion ?? "$number1 + $number2" ?> =</span>
                    <button type="button" class="refresh-security-stamp" id="refresh-security-stamp" title="Refresh Security Stamp">
                        <i class="bi bi-arrow-repeat"></i>
                    </button>
                    <?= $form->field($model, 'securityStamp')->textInput([
                        'id' => 'security-answer',
                        'placeholder' => 'Answer'
                    ])->label(false) ?>
                    <input type="hidden" id="correct-answer" name ="correct_answer" value="<?= $correctAnswer ?>">
                </div>
            </div>

            <div class="options-container">
                <div class="form-check">
                    <?= $form->field($model, 'rememberMe')->checkbox([
                        'template' => "{input} {label}",
                        'labelOptions' => ['class' => 'form-check-label'],
                        'options' => ['class' => 'form-check-input']
                    ]) ?>
                </div>
                
                <?= Html::a('<i class="bi bi-question-circle"></i> Forgot Password?', 
                    ['/ef/user/reset-password'], 
                    ['class' => 'text-decoration-none fw-medium']) ?>
            </div>

            <?= Html::submitButton('<i class="bi bi-box-arrow-in-right"></i> Login', ['class' => 'btn btn-primary', 'name' => 'login-button']) ?>
            <?= Html::a('<i class="bi bi-arrow-clockwise"></i> Reset Password', ['/ef/user/reset-password'], ['class' => 'btn btn-secondary']) ?>

            <?php ActiveForm::end(); ?>
            
            <div class="keyboard-hint">
                <i class="bi bi-keyboard"></i> Press Enter to login, Esc to reset
            </div>
        </div>
    </div>

    <div class="toast-container" id="toast-container"></div>

    <script src="https://cdn.jsdelivr.net/npm/bootstrap@5.3.0/dist/js/bootstrap.bundle.min.js"></script>
    <script>
    document.addEventListener("DOMContentLoaded", function() {
        // Display flash messages from server IMMEDIATELY
        <?php if ($error = Yii::$app->session->getFlash('error')): ?>
            console.log('Error flash message:', '<?= addslashes($error) ?>');
            showToast('<?= addslashes($error) ?>', 'error');
        <?php endif; ?>
        <?php if ($success = Yii::$app->session->getFlash('success')): ?>
            console.log('Success flash message:', '<?= addslashes($success) ?>');
            showToast('<?= addslashes($success) ?>', 'success');
        <?php endif; ?>
        <?php if ($info = Yii::$app->session->getFlash('info')): ?>
            console.log('Info flash message:', '<?= addslashes($info) ?>');
            showToast('<?= addslashes($info) ?>', 'info');
        <?php endif; ?>
        
        // Also display Yii validation errors
        <?php if ($model->hasErrors()): ?>
            <?php foreach ($model->getErrors() as $attribute => $errorMessages): ?>
                <?php foreach ($errorMessages as $error): ?>
                    console.log('Validation error:', '<?= addslashes($error) ?>');
                    showToast('<?= addslashes($error) ?>', 'error');
                <?php endforeach; ?>
            <?php endforeach; ?>
        <?php endif; ?>
        
        // Current date
        const dateElement = document.getElementById('current-date');
        if (dateElement) {
            const today = new Date();
            const options = { weekday: 'long', year: 'numeric', month: 'long', day: 'numeric' };
            dateElement.textContent = today.toLocaleDateString(undefined, options);
        }
        
        // Password visibility toggle
        const togglePassword = document.getElementById('toggle-password');
        const passwordInput = document.getElementById('password-input');
        
        if (togglePassword && passwordInput) {
            togglePassword.addEventListener('click', function() {
                if (passwordInput.type === 'password') {
                    passwordInput.type = 'text';
                    this.classList.replace('bi-eye-slash', 'bi-eye');
                } else {
                    passwordInput.type = 'password';
                    this.classList.replace('bi-eye', 'bi-eye-slash');
                }
            });
            
            // Caps lock warning
            const capsLockWarning = document.getElementById('caps-lock-warning');
            passwordInput.addEventListener('keyup', function(e) {
                capsLockWarning.style.display = e.getModifierState('CapsLock') ? 'block' : 'none';
            });
            
            // Password strength meter
            const strengthMeter = document.getElementById('strength-meter');
            const strengthMeterFill = document.getElementById('strength-meter-fill');
            const strengthLabels = document.querySelector('.strength-labels');
            const strengthText = document.getElementById('password-strength-text');
            
            passwordInput.addEventListener('input', function() {
                const val = passwordInput.value;
                
                // Show strength meter only when user starts typing
                if (val.length > 0) {
                    strengthMeter.style.display = 'block';
                    strengthLabels.style.display = 'flex';
                    strengthText.style.display = 'block';
                } else {
                    strengthMeter.style.display = 'none';
                    strengthLabels.style.display = 'none';
                    strengthText.style.display = 'none';
                    return;
                }
                
                let strength = 0;
                let strengthLabel = '';
                
                if (val.length >= 8) strength += 25;
                if (/[A-Z]/.test(val)) strength += 25;
                if (/[0-9]/.test(val)) strength += 25;
                if (/[^A-Za-z0-9]/.test(val)) strength += 25;
                
                strengthMeterFill.style.width = `${strength}%`;
                
                if (strength <= 25) {
                    strengthMeterFill.style.backgroundColor = '#e74c3c';
                    strengthLabel = 'Weak Password';
                    strengthText.className = 'password-strength-text password-strength-weak';
                } else if (strength <= 50) {
                    strengthMeterFill.style.backgroundColor = '#f39c12';
                    strengthLabel = 'Medium Password';
                    strengthText.className = 'password-strength-text password-strength-medium';
                } else if (strength <= 75) {
                    strengthMeterFill.style.backgroundColor = '#3498db';
                    strengthLabel = 'Good Password';
                    strengthText.className = 'password-strength-text password-strength-medium';
                } else {
                    strengthMeterFill.style.backgroundColor = '#27ae60';
                    strengthLabel = 'Strong Password';
                    strengthText.className = 'password-strength-text password-strength-strong';
                }
                
                strengthText.textContent = strengthLabel;
            });
        }
        
        // Toast notification function
        function showToast(message, type = 'info') {
            const toastContainer = document.getElementById('toast-container');
            const toast = document.createElement('div');
            toast.className = `toast toast-${type}`;
            
            let iconClass = '';
            if (type === 'success') iconClass = 'bi-check-circle-fill text-success';
            else if (type === 'error') iconClass = 'bi-exclamation-circle-fill text-danger';
            else iconClass = 'bi-info-circle-fill text-info';
            
            toast.innerHTML = `
                <div class="toast-header">
                    <i class="bi ${iconClass} me-2"></i>
                    <strong class="toast-title">${type.charAt(0).toUpperCase() + type.slice(1)}</strong>
                    <button type="button" class="toast-close">&times;</button>
                </div>
                <div class="toast-body">${message}</div>
            `;
            
            toastContainer.appendChild(toast);
            
            // Auto remove after 5 seconds
            setTimeout(() => {
                toast.style.animation = 'slideInRight .3s ease-out reverse';
                setTimeout(() => toast.remove(), 300);
            }, 5000);
            
            // Close button
            toast.querySelector('.toast-close').addEventListener('click', () => {
                toast.style.animation = 'slideInRight .3s ease-out reverse';
                setTimeout(() => toast.remove(), 300);
            });
        }
        
        // Security stamp validation
        const loginForm = document.getElementById('login-form');
        const loginButton = loginForm.querySelector('button[type="submit"]');
        
        loginForm?.addEventListener('submit', function(e) {
            console.log('Form submitting...');
            const userAnswer = parseInt(document.getElementById('security-answer').value, 10);
            const correctAnswer = parseInt(document.getElementById('correct-answer').value, 10);
            
            console.log('User answer:', userAnswer, 'Correct answer:', correctAnswer);
            
            if (isNaN(userAnswer) || userAnswer !== correctAnswer) {
                e.preventDefault();
                console.log('Security stamp validation failed - preventing submit');
                showToast('Incorrect Security Stamp. Please try again.', 'error');
                
                const securityStampField = document.getElementById('security-answer');
                securityStampField.classList.add('is-invalid');
                securityStampField.focus();
                
                setTimeout(() => {
                    securityStampField.classList.remove('is-invalid');
                }, 1000);
                return false;
            } else {
                console.log('Security stamp valid - allowing submit');
                loginButton.classList.add('btn-loading');
                loginButton.disabled = true;
                showToast('Authenticating...', 'info');
                // Allow form to submit
                return true;
            }
        });
        
        // Refresh security stamp
        const refreshBtn = document.getElementById('refresh-security-stamp');
        refreshBtn?.addEventListener('click', function() {
            const num1 = Math.floor(Math.random() * 10) + 1;
            const num2 = Math.floor(Math.random() * 20) + 1;
            const answer = num1 + num2;
            
            const questionElement = document.getElementById('security-question');
            questionElement.style.opacity = '0';
            
            setTimeout(() => {
                questionElement.textContent = `${num1} + ${num2} =`;
                questionElement.style.opacity = '1';
            }, 300);
            
            document.getElementById('correct-answer').value = answer;
            document.getElementById('security-answer').value = '';
            showToast('Security stamp refreshed', 'success');
        });
        
        // Keyboard shortcuts
        document.addEventListener('keydown', function(e) {
            // Enter to submit
            if (e.key === 'Enter' && document.activeElement.tagName !== 'BUTTON') {
                e.preventDefault();
                loginForm.dispatchEvent(new Event('submit'));
            }
            
            // Escape to reset
            if (e.key === 'Escape') {
                loginForm.reset();
                document.getElementById('security-answer').value = '';
                document.getElementById('strength-meter-fill').style.width = '0';
                document.getElementById('password-strength-text').textContent = '';
                document.getElementById('strength-meter').style.display = 'none';
                document.querySelector('.strength-labels').style.display = 'none';
                document.getElementById('password-strength-text').style.display = 'none';
                showToast('Form reset', 'info');
            }
        });
        
        // Remember username functionality
        const rememberMeCheckbox = document.querySelector('input[name="LoginForm[rememberMe]"]');
        const usernameInput = document.querySelector('input[name="LoginForm[username]"]');
        
        // Load saved username if exists
        const savedUsername = localStorage.getItem('rememberedUsername');
        if (savedUsername && rememberMeCheckbox) {
            usernameInput.value = savedUsername;
            rememberMeCheckbox.checked = true;
        }
        
        // Save username when "Remember me" is checked
        rememberMeCheckbox?.addEventListener('change', function() {
            if (this.checked) {
                localStorage.setItem('rememberedUsername', usernameInput.value);
            } else {
                localStorage.removeItem('rememberedUsername');
            }
        });
        
        // Update saved username when it changes
        usernameInput?.addEventListener('input', function() {
            if (rememberMeCheckbox.checked) {
                localStorage.setItem('rememberedUsername', this.value);
            }
        });
        
        // Form field animations
        const formInputs = document.querySelectorAll('.form-control');
        formInputs.forEach(input => {
            input.addEventListener('focus', function() {
                this.parentElement.classList.add('focused');
            });
            
            input.addEventListener('blur', function() {
                this.parentElement.classList.remove('focused');
            });
        });
        
        // Auto-refresh security stamp every 60 seconds
        setInterval(() => {
            if (document.visibilityState === 'visible') {
                refreshBtn.click();
            }
        }, 60000);
    });
    </script>
</body>
</html>